import React, { useEffect, useState } from "react";
import fakeData from "../../fakeData/MOCK_DATA.json";
import DisplayData from "../DisplayData/DisplayData";
import "./InputData.css";
import AddedTeamPlayer from "../AddedTeamPlayer/AddedTeamPlayer";
const InputData = () => {
  //Data useState
  const [users, setUsers] = useState([]);
  useEffect(() => {
    setUsers(fakeData);
  }, []);
//Event handlers useState
let myTeam = [];
const [players, setPlayers] = useState([]);
useEffect(() => {
  setPlayers(myTeam);
},[])
//Event handlers use
  const handlePlyer = (newPlyer) => {
      myTeam.push(newPlyer)

  };

  return (
    <div className="container-fluid">
      <div className="row">
        <div className="col-md-8">
          <div className="container">
            <div className="row">
              <div className="col-md-3">
                {users.map((user) => (
                  <DisplayData
                    handlePlyer={handlePlyer}
                    users={user}
                  ></DisplayData>
                ))}
              </div>
            </div>
          </div>
        </div>
        <div className="col-md-4">
          <div>
            <AddedTeamPlayer></AddedTeamPlayer>
            <h1>payer {players.name}</h1>
          </div>
        </div>
      </div>
    </div>
  );
};

export default InputData;
