/* eslint-disable react-hooks/exhaustive-deps */
import React, { useEffect, useState } from "react";
import fakeData from "../../fakeData/MOCK_DATA.json";
import DisplayData from "../DisplayData/DisplayData";
import "./InputData.css";
import AddedTeamPlayer from "../AddedTeamPlayer/AddedTeamPlayer";
const InputData = () => {
  //Data useState
  const [users, setUsers] = useState([]);
  const [teams,setTeams] = useState([]);
  useEffect(() => {
    setUsers(fakeData);
  }, []);

//Event handlers use
  const handlePlyer = (newPlyer) => {
     const myTeam = [...teams, newPlyer]
     setTeams(myTeam);
  };
  console.log(teams)
  return (
    <div className="container-fluid">
      <div className="row">
        <div className="col-md-8">
          <div className="container">
            <div className="row">
              <div className="col-md-3">
                {users.map((user) => (
                  <DisplayData
                    handlePlyer={handlePlyer}
                    users={user}
                  ></DisplayData>
                ))}
              </div>
            </div>
          </div>
        </div>
        <div className="col-md-4">
          <div>
            <AddedTeamPlayer></AddedTeamPlayer>

          </div>
        </div>
      </div>
    </div>
  );
};

export default InputData;
